HassGetState.any:
{% if query.matched %}
  {% set match = query.matched | map(attribute="name") | sort | list %}
  {% if match | length > 4 %}
    Da, {{ match[:3] | join(", ") }} in še {{ (match | length - 3) }}
  {%- else -%}
    Da,
    {% for name in match -%}
      {% if not loop.first and not loop.last %}, {% elif loop.last and not loop.first %} in {% endif -%}
      {{ name }}
    {%- endfor -%}
  {% endif %}
{% else %}
  Ne
{% endif %}

HassGetState.all:
{% if not query.unmatched: %}
  Da
{% else %}
  {% set no_match = query.unmatched | map(attribute="name") | sort | list %}
  Ne,
  {% if no_match | length == 2 %}
    nista
  {%- elif no_match | length > 2 %}
    niso
  {%- else -%}
    ni
  {% endif %}
  {% for name in no_match -%}
    {% if not loop.first and not loop.last %}, {% elif loop.last and not loop.first %} in {% endif -%}
    {{ name }}
  {%- endfor %}
{% endif %}

HassGetState.all_bs:
{% if not query.unmatched: %}
  Da
{% else %}
  {% set no_match = query.unmatched | map(attribute="name") | sort | list %}
  Ne,
  {% if 'vrata' in no_match %}
    {{ no_match[:-1] | join(' ') }} niso
  {%- else %}
    {{ no_match[-1] }} ni
  {% endif %}
  {% for name in no_match[0:-1] -%}
    {% if not loop.first %}, {% endif -%}
    {{ name }}
  {%- endfor %}
{% endif %}

